using System; using System.Windows.Forms; namespace Bai6_3 { public partial class Form1 : Form { public Form1() { InitializeComponent(); cbLop.SelectedIndex = 0; } private void btnThem_Click(object sender, EventArgs e) { string ma = txtMa.Text.Trim(); string ten = txtHoTen.Text.Trim(); string diachi = txtDiaChi.Text.Trim(); string ngay = dateTimePicker1.Value.ToShortDateString(); string lop = cbLop.Text; if(string.IsNullOrWhiteSpace(ten)) { MessageBox.Show("Họ tên không được để trống!", "Lỗi", MessageBoxButtons.OK, MessageBoxIcon.Warning); return; } int idx = dataGridView1.Rows.Add(); dataGridView1.Rows[idx].Cells[0].Value = ma; dataGridView1.Rows[idx].Cells[1].Value = ten; dataGridView1.Rows[idx].Cells[2].Value = diachi; dataGridView1.Rows[idx].Cells[3].Value = ngay; dataGridView1.Rows[idx].Cells[4].Value = lop; txtMa.Clear(); txtHoTen.Clear(); txtDiaChi.Clear(); } private void btnXoa_Click(object sender, EventArgs e) { foreach (DataGridViewRow r in dataGridView1.SelectedRows) dataGridView1.Rows.Remove(r); } private void btnThoat_Click(object sender, EventArgs e) => this.Close(); } }