using System; using System.Windows.Forms; namespace Bai6_2 { public partial class Form1 : Form { public Form1() { InitializeComponent(); } private void btnThem_Click(object sender, EventArgs e) { var name = txtHoTen.Text.Trim(); if (string.IsNullOrEmpty(name)) { MessageBox.Show("Bạn chưa nhập dữ liệu", "Thông báo", MessageBoxButtons.OK, MessageBoxIcon.Information); return; } if (rdLopA.Checked) lstLopA.Items.Add(name); else lstLopB.Items.Add(name); txtHoTen.Clear(); txtHoTen.Focus(); } private void btnChuyenMot_Click(object sender, EventArgs e) => MoveSelected(lstLopA, lstLopB); private void btnChuyenMotBack_Click(object sender, EventArgs e) => MoveSelected(lstLopB, lstLopA); private void btnChuyenAll_Click(object sender, EventArgs e) => MoveAll(lstLopA, lstLopB); private void btnChuyenAllBack_Click(object sender, EventArgs e) => MoveAll(lstLopB, lstLopA); private void btnTong_Click(object sender, EventArgs e) { int total = lstLopA.Items.Count + lstLopB.Items.Count; MessageBox.Show("Tổng số sinh viên: " + total, "Tổng", MessageBoxButtons.OK, MessageBoxIcon.Information); } private void btnThoat_Click(object sender, EventArgs e) => this.Close(); private void MoveSelected(ListBox from, ListBox to) { var items = from.SelectedItems; var temp = new object[items.Count]; items.CopyTo(temp, 0); foreach (var it in temp) to.Items.Add(it); foreach (var it in temp) from.Items.Remove(it); } private void MoveAll(ListBox from, ListBox to) { foreach (var it in from.Items) to.Items.Add(it); from.Items.Clear(); } 
        
} } 